package ex01변수;

public class Ex01변수개요 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 한줄 주석
		// 컴퓨터가 실행하지 않고 넘어가는 부분
		// 보통 주석에는 코드의 설명을 작성함
		
		/* 
		 여러줄 주석
		 */
		
		// Hello World를 콘솔창에 출력하기
		// println 은 개행 해줌 , 단축키 syso, ctrl+space 자동완성
		System.out.println("Hello World!");
		System.out.println("안녕 세상!");
		
		// 정수형 변수 num에 10을 저장
		// 변수자료형 , 변수명 , 대입연산자, 대입할 값
		// ctrl+alt+아래화살표 : 아래행으로 복붙됨
		int num = 10;
		System.out.println(num);	
		
	}

}
