package ex01변수;

public class Ex04변수추가 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 상수 : 절대로 값이 변하지 않는 변수 "final"
		// 값을 절대 수정할 수 없음
		
		final double pi = 3.141592;
		
		// 문자열을 저장하는 방법
		// 문자열이랑 2글자 이상 해당되는 글자
		// String : 레퍼런스 자료형(기본자료형x)
		
		String text = "안녕하세요 헬로우 하이:)";
		System.out.println(text);
		
	}

}
