package ex02연산자;

public class Ex06비교연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 10;
		int num2 = 5;
		int num3 = 10;
		int num4 = 5;
		// 비교연산자 값은 true/false 로 나옴
		System.out.println(num1 > num2);
		System.out.println(num1 < num2);
		System.out.println(num1 == num2);
		System.out.println(num1 != num2);   //다르다 , 같지 않다
		System.out.println(num1 >= num2);
		System.out.println(num1 <= num2);
		
	}

}
